import { ClassicRuleset } from "./core/ClassicRuleset.js";
import { CpuPlayer } from "./core/CpuPlayer.js";
import { HumanPlayer } from "./core/HumanPlayer.js";
import { ClassicMatch } from "./core/ClassicMatch.js";
import { ClassicGameSession } from "./core/ClassicGameSession.js";
import { UserInterfaceManager } from "./core/UserInterfaceManager.js";
const player = new HumanPlayer();
const cpu = new CpuPlayer();
const classicRuleset = new ClassicRuleset();
const gameMatch = new ClassicMatch();
const userInterface = new UserInterfaceManager();
const classicGameSession = new ClassicGameSession(gameMatch, player, cpu, classicRuleset, userInterface);
classicGameSession.startGame();
